	-- if we didn't create a brand new order for a customer shipment, translate the old shipment's OrderID
	IF @newOrderKey IS NULL 
	BEGIN
		SET @newOrderKey = dbo.v2m_TranslateKey(@sOrderID, 0 /* order */)
	END

