				WHEN @fedCustomsCanadaPackageType = 9 THEN 9 -- chest -> crate
				WHEN @fedCustomsCanadaPackageType = 10 THEN 9 -- chest -> crate
				WHEN @fedCustomsCanadaPackageType = 11 THEN 9 -- crate -> crate
