				WHEN @fedCustomsCanadaPackageType = 24 THEN 20 -- skid -> skid
				WHEN @fedCustomsCanadaPackageType = 25 THEN 21 -- tank -> tank
				WHEN @fedCustomsCanadaPackageType = 26 THEN 6 -- totebin -> carton
