-- This script will remove all non-thermal shipping labels older than a certain date
-- 
-- This is done by adding png and gif resources to ShipWorks that say "This resource has been deleted"
-- and then pointing all of the expired labels to this resource while deleting the old ones.
-- 
-- * Edit the line SET @deleteOlderThan = ..... below to set the threshold.
--
-- * Change the database name in the DBCC SHRINKDATABASE command at the bottom
-- 
DECLARE 
	@deleteOlderThan datetime,
	@oldCount int,
	@deleteLabelResourceID_PNG bigint,
	@deleteLabelResourceID_GIF bigint	
	
SET @deleteOlderThan = '{CUTOFFDATE}'		--	yyyy-mm-dd

-- if this is the first time we've run this, figure out which resources must go
IF OBJECT_ID('tempdb..#resourceworking') IS NULL
BEGIN
	-- create temp table
	CREATE TABLE #resourceworking ( 
		ObjectReferenceID bigint,
		ResourceID bigint,
		ImageFormat nvarchar(5)
	)

	-- find all of the UPS labels we want to wipe out
	INSERT INTO #resourceworking
		SELECT r.ObjectReferenceID AS ObjectReferenceID, ObjectID AS ResourceID, 'GIF' as ImageFormat
		FROM [UpsPackage] p, [Shipment] s, [ObjectReference] r
		WHERE 
		p.ShipmentID = s.ShipmentID
		AND p.UpsPackageID = r.ConsumerID	
		AND s.ProcessedDate < @deleteOlderThan
		AND s.Processed = 1
		AND s.ActualLabelFormat IS NULL -- don't wipe thermals
		AND s.ShipmentType = 0	-- UPS online tools

	-- find all of the fedex labels we want to wipe out
	INSERT INTO #resourceworking		
		SELECT r.ObjectReferenceID AS ObjectReferenceID, ObjectID AS ResourceID, 'PNG' as ImageFormat
		FROM [FedexPackage] p, [Shipment] s, [ObjectReference] r
		WHERE 
		p.ShipmentID = s.ShipmentID
		AND p.FedexPackageID = r.ConsumerID		
		AND s.ProcessedDate < @deleteOlderThan	
		AND s.Processed = 1
		AND s.ActualLabelFormat IS NULL -- don't wipe thermals
		AND s.ShipmentType = 6	-- FedEx
		
	-- Endicia labels
	INSERT INTO #resourceworking		
		SELECT r.ObjectReferenceID AS ObjectReferenceID, ObjectID AS ResourceID, 'PNG' as ImageFormat
		FROM [PostalShipment] p, [Shipment] s, [ObjectReference] r
		WHERE 
		p.ShipmentID = s.ShipmentID		
		AND p.ShipmentID = r.ConsumerID		
		AND s.ProcessedDate < @deleteOlderThan
		AND s.Processed = 1
		AND s.ActualLabelFormat IS NULL
		AND s.ShipmentType = 2 -- endicia

	-- Express1 labels
	INSERT INTO #resourceworking		
		SELECT r.ObjectReferenceID AS ObjectReferenceID, ObjectID AS ResourceID, 'PNG' as ImageFormat
		FROM [PostalShipment] p, [Shipment] s, [ObjectReference] r
		WHERE 
		p.ShipmentID = s.ShipmentID		
		AND p.ShipmentID = r.ConsumerID		
		AND s.ProcessedDate < @deleteOlderThan
		AND s.Processed = 1
		AND s.ActualLabelFormat IS NULL
		AND s.ShipmentType = 9 -- express1
		
	-- Usps
	INSERT INTO #resourceworking		
		SELECT r.ObjectReferenceID AS ObjectReferenceID, ObjectID AS ResourceID, 'PNG' as ImageFormat
		FROM [PostalShipment] p, [Shipment] s, [ObjectReference] r
		WHERE 
		p.ShipmentID = s.ShipmentID
		AND p.ShipmentID = r.ConsumerID		
		AND s.ProcessedDate < @deleteOlderThan
		AND s.Processed = 1
		AND s.ActualLabelFormat IS NULL
		AND s.ShipmentType = 15 -- usps
	
	--Without Postage
	INSERT INTO #resourceworking		
		SELECT r.ObjectReferenceID AS ObjectReferenceID, ObjectID AS ResourceID, 'PNG' as ImageFormat
		FROM [PostalShipment] p, [Shipment] s, [ObjectReference] r
		WHERE 
		p.ShipmentID = s.ShipmentID
		AND p.ShipmentID = r.ConsumerID		
		AND s.ProcessedDate < @deleteOlderThan
		AND s.Processed = 1
		AND s.ActualLabelFormat IS NULL
		AND s.ShipmentType = 4 --w/o postage

	CREATE INDEX IX_ResourceWorking on #resourceworking (ObjectReferenceID, ResourceID)		
END

-- insert the Deleted image (png format)
SELECT @oldCount = COUNT(*) FROM [Resource] WHERE [Filename] = '__ResourceCleanup_png.swr'
IF (@oldCount = 0)
BEGIN
	
	INSERT INTO [Resource] ([Data], [Checksum], [Compressed], [Filename])
	VALUES(,
	0x4BC1D33D787336CE644DA23DCFE116FD5A432B876BA8AFABC455B9813941EAD0,
	0,
	'__ResourceCleanup_png.swr');
	
	SET @deleteLabelResourceID_PNG = @@IDENTITY
END
ELSE
BEGIN
	-- we've run it once, so just locate the resource id we are redirecting to
	SELECT @deleteLabelResourceID_PNG = ResourceID FROM [Resource] WHERE [Filename] = '__ResourceCleanup_png.swr'
END

-- insert the Deleted image (png format)
SELECT @oldCount = COUNT(*) FROM [Resource] WHERE [Filename] = '__ResourceCleanup_gif.swr'
IF (@oldCount = 0)
BEGIN
	-- insert the Deleted image (gif format)
	INSERT INTO [Resource] ([Data], [Checksum], [Compressed], [Filename])
	VALUES(,
	0xA1349D0C4FBC71040684EDDE8922162CD392D8B5E00EEEC0C86FDA9C37344A5B,
	0,
	'__ResourceCleanup_gif.swr');

	SET @deleteLabelResourceID_GIF = @@IDENTITY
END
ELSE
BEGIN
	SELECT @deleteLabelResourceID_GIF = ResourceID FROM [Resource] WHERE [Filename] = '__ResourceCleanup_gif.swr'
END
	
-- see if there's any actual work to do
SELECT @oldCount = COUNT(*) FROM #resourceworking;
IF @oldCount > 0
BEGIN

	DECLARE @objectReferenceID bigint,
			@resourceID bigint,
			@imageFormat nvarchar(10)

	-- create cursor
	DECLARE workCursor CURSOR FOR
	SELECT /* top 5000 */ * FROM #resourceworking
	
	-- open cursor
	OPEN workCursor

	-- start looping
	FETCH NEXT FROM workCursor
	INTO
	@objectReferenceID,
	@resourceID,
	@imageFormat
	WHILE @@FETCH_STATUS = 0
	BEGIN
		IF (@resourceID <> @deleteLabelResourceID_GIF AND @resourceID <> @deleteLabelResourceID_PNG)
		BEGIN		
			BEGIN TRANSACTION
			
			-- adjust the old reference to point to the new "deleted" image resource, by image format
			IF @imageFormat = 'PNG'
			BEGIN
				UPDATE [ObjectReference] SET ObjectID = @deleteLabelResourceID_PNG
				WHERE ObjectReferenceID = @objectReferenceID
			END
			ELSE
			BEGIN
				UPDATE [ObjectReference] SET ObjectID = @deleteLabelResourceID_GIF
				WHERE ObjectReferenceID = @objectReferenceID			
			END
		
			-- delete the old resources
			DELETE FROM [Resource] WHERE ResourceID = @resourceID
			
			-- delete from our temp table
			DELETE FROM #resourceworking WHERE ObjectReferenceID = @objectReferenceID AND ResourceID = @resourceID
			
			COMMIT TRANSACTION
		END
		ELSE
		BEGIN
			-- this one was already converted on a previous run of the script, so just remove it from the temp table			
			DELETE FROM #resourceworking WHERE ObjectReferenceID = @objectReferenceID AND ResourceID = @resourceID
		END

			
		-- move to the next
		FETCH NEXT FROM workCursor
		INTO
		@objectReferenceID,
		@resourceID,
		@imageFormat						
	END

	CLOSE workCursor
	DEALLOCATE workCursor
	
	Print 'Reindexing Resources'
	DBCC DBREINDEX([Resource])
	
	Print 'Shrinking Database'
	DBCC SHRINKDATABASE({DATABASENAME})
END
